template <int group = 20, typename ITER>
void show(const char* note, ITER begin, ITER end) {
    cout << note;
    for (int i = 1; begin != end; i++) {
        std::cout << *(begin++) << ' ';
        if (i % group == 0 or begin == end) std::cout << std::endl;
    }
